mapper.select:
SELECT * FROM user

    id=#{id}
    AND username like #{username}
    AND email like #{email}
    AND address like #{address}

ORDER BY id
LIMIT #{pageNumber}, #{pageSize}

SELECT COUNT(id) FROM user

    id=#{id}
    AND username like #{username}
    AND email like #{email}
    AND address like #{address}

ORDER BY id
LIMIT #{pageNumber}, #{pageSize}

mapper.insert:

INSERT INTO user (username, password, email, address, created_at, updated_at, created_by, updated_by) VALUES (
  #{username}
  ,#{password}
  ,#{email}
  ,#{address}
  ,#{createdAt}
  ,#{updatedAt}
  ,#{createdBy}
  ,#{updatedBy}
)

mapper.update:
UPDATE user
username=#{username}, password=#{password}, email=#{email}, address=#{address},
WHERE id = #{id}
